mapper.select:
select * from user_like
where user_id = #{userId}
  and target_id = #{targetId}
  and target_type = #{targetType}
  and is_delete = 0
    limit 1

select * from user_like
where user_id = #{userId}

    and target_type = #{targetType}

and is_delete = 0
order by create_time desc

mapper.update:
update user_like
set is_delete = 1
where id = #{id}

mapper.select:
select target_id from user_like
where user_id = #{userId}
  and target_type = #{targetType}
  and is_delete = 0
